css:
.print-format .header-table{
    width:100% !important;

}

.print-format .header-table td img{
    width: 30% !important;
}

.print-format .header-table td.brand{
    padding-left: 0 !important;
    font-size: 18pt !important;
    padding-bottom: 1rem !important;
}
.print-format .header-table td{
    padding-left: 0 !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    color: black !important;
    font-weight: 600 !important;
}

.print-format .info-table{
    width:100% !important;
}

.print-format .info-table table{
    width:100% !important;
    font-size: 10pt !important;
    color: black !important;
}

.print-format .info-table td{
    padding-left: 0 !important;
    padding-top: 6px !important;
    padding-bottom: 6px !important;
}

.print-format .line{
    margin-top: 0.7rem !important;
    margin-bottom: 1rem !important;
    border-top:solid 1px;
}

.print-format .item-table{
    width:100% !important;
    margin-bottom: 1rem !important;
}
.print-format .item-table th, .print-format .item-table td{
    font-size: 10pt !important;
    border: 1px solid black;
    color: black !important;
    padding: 3px !important;
}
.print-format .item-table th{
    font-weight: bold !important;
    color: black !important;
}

.print-format .total-table{
    width: 22% !important;
    margin-bottom: 0.5rem !important;
}
.print-format .total-table td{
    padding-left: 0 !important;
    padding-bottom: 0 !important;
    padding-top: 2px !important;
    font-size: 10pt !important;
    color: black !important;
}

.print-format .deliver-table{
    width: 40% !important;
    margin-bottom: 1rem !important;
}

.print-format .deliver-table td{
    padding-left: 0 !important;
    padding-bottom: 0 !important;
     padding-top: 2px !important;
    font-size: 10pt !important;
    color: black !important;
}
